Ext.define("Ext.draw.gradient.Linear",{extend:"Ext.draw.gradient.Gradient",type:"linear",config:{degrees:0},setAngle:function(A){this.setDegrees(A)},generateGradient:function(G,M){var K=Ext.draw.Draw.rad(this.getDegrees()),F=Math.cos(K),D=Math.sin(K),B=M.width,I=M.height,A=M.x+B*0.5,E=M.y+I*0.5,N=this.getStops(),C=N.length,H,J,L;if(!isNaN(A)&&!isNaN(E)&&I>0&&B>0){J=(Math.sqrt(I*I+B*B)*Math.abs(Math.cos(K-Math.atan(I/B))))/2;H=G.createLinearGradient(A+F*J,E+D*J,A-F*J,E-D*J);for(L=0;L<C;L++){H.addColorStop(N[L].offset,N[L].color)}return H}return"none"}});