Ext.define("Ext.fx.easing.Linear",{extend:"Ext.fx.easing.Abstract",alias:"easing.linear",config:{duration:0,endValue:0},updateStartValue:function(A){this.distance=this.getEndValue()-A},updateEndValue:function(A){this.distance=A-this.getStartValue()},getValue:function(){var B=Ext.Date.now()-this.getStartTime(),A=this.getDuration();if(B>A){this.isEnded=true;return this.getEndValue()}else{return this.getStartValue()+((B/A)*this.distance)}}});