Ext.define("Ext.form.RadioManager",{extend:"Ext.util.MixedCollection",singleton:true,getByName:function(B,A){return this.filterBy(function(C){return C.name==B&&C.getFormId()==A})},getWithValue:function(B,C,A){return this.filterBy(function(D){return D.name==B&&D.inputValue==C&&D.getFormId()==A})},getChecked:function(B,A){return this.findBy(function(C){return C.name==B&&C.checked&&C.getFormId()==A})}});